INSERT INTO blogs VALUES(200,'2002-colima-trip-2002-part-4','Colima Trip 2002 Part 4','2014-11-18 02:11:30.374351','2015-05-25 20:54:16.318932','<p><strong>Christmas Eve/Navidad</strong></p>
